// This file can be considered as a mixin for ReactBingMaps
// It contains the more complex state manipulation aspects of that class in order to reduce overall file size

// it exports functions rather than lambdas so that they can be bound to the class and thus access `this`

export function setMapCenter(center, mapReference) {
  if (map[mapReference] && center && center[0] && center[1]) {
    map[mapReference].setView({
      center: new Microsoft.Maps.Location(center[0], center[1])
    });
  }
}

